  `path` text COLLATE utf8mb4_unicode_520_ci NOT NULL COMMENT 'Request Path or URI',
  `code` int(5) NOT NULL DEFAULT '200' COMMENT 'HTTP Response Code',
  `verb` varchar(10) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT 'get' COMMENT 'HTTP Method',
  `ua` text COLLATE utf8mb4_unicode_520_ci COMMENT 'Browser User Agent String',
  `trans` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT 'Trangression',
  `created_at` int(15) unsigned NOT NULL DEFAULT '0' COMMENT 'Created At',
  `deleted_at` int(15) unsigned NOT NULL DEFAULT '0' COMMENT 'Soft Deleted At',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_520_ci;


DROP TABLE IF EXISTS `wp_links`;
CREATE TABLE `wp_links` (
  `link_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `link_url` varchar(255) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT '',
  `link_name` varchar(255) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT '',
  `link_image` varchar(255) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT '',
  `link_target` varchar(25) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT '',
  `link_description` varchar(255) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT '',
  `link_visible` varchar(20) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT 'Y',
  `link_owner` bigint(20) unsigned NOT NULL DEFAULT '1',
  `link_rating` int(11) NOT NULL DEFAULT '0',
  `link_updated` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `link_rel` varchar(255) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT '',
  `link_notes` mediumtext COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `link_rss` varchar(255) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT '',
  PRIMARY KEY (`link_id`),
  KEY `link_visible` (`link_visible`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_520_ci;


DROP TABLE IF EXISTS `wp_odb_logs`;
CREATE TABLE `wp_odb_logs` (
  `odb_id` int(11) NOT NULL AUTO_INCREMENT,
  `odb_timestamp` varchar(20) NOT NULL,
  `odb_revisions` int(11) NOT NULL,
  `odb_trash` int(11) NOT NULL,
  `odb_spam` int(11) NOT NULL,
  `odb_tags` int(11) NOT NULL,
  `odb_transients` int(11) NOT NULL,
  `odb_pingbacks` int(11) NOT NULL,
  `odb_oembeds` int(11) NOT NULL,
  `odb_orphans` int(11) NOT NULL,
  `odb_tables` int(11) NOT NULL,
  `odb_before` varchar(20) NOT NULL,
  `odb_after` varchar(20) NOT NULL,
  `odb_savings` varchar(20) NOT NULL,
  PRIMARY KEY (`odb_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `wp_options`;
CREATE TABLE `wp_options` (
  `option_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `option_name` varchar(191) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT '',
  `option_value` longtext COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `autoload` varchar(20) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT 'yes',
  PRIMARY KEY (`option_id`),
  UNIQUE KEY `option_name` (`option_name`),
  KEY `autoload` (`autoload`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_520_ci;

